import { BehaviorSubject, firstValueFrom, map } from 'rxjs';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class CompaniesService {
    constructor(httpClient) {
        this.httpClient = httpClient;
        this.cache = new Map();
        this.selectedCompaniesDomains$ = new BehaviorSubject([]);
        this.selectedCompanies$ = this.selectedCompaniesDomains$.pipe(map(domains => domains.map(domain => this.cache.get(domain))));
    }
    async addSelectedCompany(domain) {
        if (this.cache.has(domain)) {
            return this.cache.get(domain);
        }
        const res = await this.fetchCompanyData();
        if (res.success) {
            this.cache.set(domain, res.data);
        }
        this.selectedCompaniesDomains$.next([...this.selectedCompaniesDomains$.value, domain]);
    }
    async getCompany(domain) {
        return this.cache.get(domain);
    }
    async fetchCompanyData() {
        return await firstValueFrom(this.httpClient.get("http://localhost:3000/company?domain=google.com"));
    }
}
